CREATE VIEW project_report as select `a`.`actual_dt_created` AS `created_at`,`a`.`dt_created` AS `last_updated_at`,`a`.`due_date` AS `due_date`,`a`.`title` AS `title`,`a`.`message` AS `message`,`b`.`name` AS `project_name`,`c`.`name` AS `task_type`,`a`.`type_id` AS `type_id`,`d`.`name` AS `created_by`,`e`.`name` AS `assigned_to`,`a`.`user_id` AS `user_id`,`a`.`project_id` AS `project_id`,`a`.`assign_to` AS `assign_to`,case when `a`.`legend` = 1 then 'New' when `a`.`legend` = 2 then 'Opened' when `a`.`legend` = 3 then 'Closed' when `a`.`legend` = 4 then 'Start' when `a`.`legend` = 5 then 'Resolve' when `a`.`legend` = 6 then 'Modified' end AS `final_status`,case when `a`.`reply_type` = 4 then 'High Priority' else 'Default Priority' end AS `priority`,case when `a`.`status` = 0 then 'Pending' else 'Closed' end AS `status` from ((((`project_`.`easycases` `a` join `project_`.`projects` `b` on(`b`.`id` = `a`.`project_id`)) join `project_`.`types` `c` on(`c`.`id` = `a`.`type_id`)) join `project_`.`users` `d` on(`d`.`id` = `a`.`user_id`)) join `project_`.`users` `e` on(`e`.`id` = `a`.`assign_to`)) where `a`.`istype` = 1;
